(52, '52.jpg', 'Belleville Washers', '<div class=\"\">\n    <h3 class=\"title\">Belleville Washers Size Guide and Specifications</h3>\n\n    <p>\n        <strong>DYN Fasteners</strong> supplies high-quality <strong>Belleville washers</strong>, also known as conical spring washers, designed to provide high load capacity and uniform tension distribution. These washers are ideal for applications requiring vibration resistance and thermal expansion compensation.\n    </p>\n\n    <h4>Belleville Washer Sizes</h4>\n    <table border=\"1\" cellpadding=\"6\" cellspacing=\"0\" style=\"width:100%; border-collapse:collapse; text-align:center;\">\n        <thead style=\"background-color:#f2f2f2;\">\n            <tr>\n                <th>Inner Diameter (mm)</th>\n                <th>Outer Diameter (mm)</th>\n                <th>Thickness (mm)</th>\n            </tr>\n        </thead>\n        <tbody>\n            <tr>\n                <td>5</td>\n                <td>10</td>\n                <td>1.5</td>\n            </tr>\n            <tr>\n                <td>6</td>\n                <td>12</td>\n                <td>1.5</td>\n            </tr>\n            <tr>\n                <td>8</td>\n                <td>16</td>\n                <td>1.5</td>\n            </tr>\n            <tr>\n                <td>10</td>\n                <td>20</td>\n                <td>2</td>\n            </tr>\n            <tr>\n                <td>12</td>\n                <td>24</td>\n                <td>2</td>\n            </tr>\n            <tr>\n                <td>Custom Sizes</td>\n                <td>Available upon request</td>\n                <td>Available upon request</td>\n            </tr>\n        </tbody>\n    </table>\n\n    <p>\n        Custom sizes are available upon request, depending on project requirements.\n    </p>\n</div>\n', 'washers'),
(53, '53.jpg', 'Wave Washers', '<div class=\"\">\r\n    <h3 class=\"title\">Wave Washers Size Guide and Specifications</h3>\r\n\r\n    <p>\r\n        <strong>DYN Fasteners</strong> offers premium-quality <strong>wave washers</strong> designed to provide flexible preload and absorb shock or vibration in bolted assemblies. These washers are ideal for applications requiring consistent spring pressure in limited spaces.\r\n    </p>\r\n\r\n    <h4>Wave Washer Sizes</h4>\r\n    <table border=\"1\" cellpadding=\"6\" cellspacing=\"0\" style=\"width:100%; border-collapse:collapse; text-align:center;\">\r\n        <thead style=\"background-color:#f2f2f2;\">\r\n            <tr>\r\n                <th>Inner Diameter (mm)</th>\r\n                <th>Outer Diameter (mm)</th>\r\n                <th>Thickness (mm)</th>\r\n            </tr>\r\n        </thead>\r\n        <tbody>\r\n            <tr>\r\n                <td>5</td>\r\n                <td>10</td>\r\n                <td>0.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>6</td>\r\n                <td>12</td>\r\n                <td>0.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>8</td>\r\n                <td>16</td>\r\n                <td>0.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>10</td>\r\n                <td>20</td>\r\n                <td>1</td>\r\n            </tr>\r\n            <tr>\r\n                <td>12</td>\r\n                <td>24</td>\r\n                <td>1</td>\r\n            </tr>\r\n            <tr>\r\n                <td>Custom Sizes</td>\r\n                <td>Available upon request</td>\r\n                <td>Available upon request</td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n\r\n    <p>\r\n        Custom sizes are available upon request, depending on project requirements.\r\n    </p>\r\n</div>\r\n', 'washers'),
(54, '54.jpg', 'Fender Washers ', '<div class=\"\">\r\n    <h3 class=\"title\">Fender Washers Size Guide and Specifications</h3>\r\n\r\n    <p>\r\n        <strong>DYN Fasteners</strong> provides high-quality <strong>fender washers</strong> designed with a larger outer diameter for superior load distribution. These washers are ideal for sheet metal, plumbing, electrical, and automotive applications where extra bearing surface is required.\r\n    </p>\r\n\r\n    <h4>Fender Washer Sizes</h4>\r\n    <table border=\"1\" cellpadding=\"6\" cellspacing=\"0\" style=\"width:100%; border-collapse:collapse; text-align:center;\">\r\n        <thead style=\"background-color:#f2f2f2;\">\r\n            <tr>\r\n                <th>Inner Diameter (mm)</th>\r\n                <th>Outer Diameter (mm)</th>\r\n                <th>Thickness (mm)</th>\r\n            </tr>\r\n        </thead>\r\n        <tbody>\r\n            <tr>\r\n                <td>5</td>\r\n                <td>12</td>\r\n                <td>1.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>6</td>\r\n                <td>15</td>\r\n                <td>1.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>8</td>\r\n                <td>20</td>\r\n                <td>2</td>\r\n            </tr>\r\n            <tr>\r\n                <td>10</td>\r\n                <td>25</td>\r\n                <td>2</td>\r\n            </tr>\r\n            <tr>\r\n                <td>12</td>\r\n                <td>30</td>\r\n                <td>2.5</td>\r\n            </tr>\r\n            <tr>\r\n                <td>Custom Sizes</td>\r\n                <td>Available upon request</td>\r\n                <td>Available upon request</td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n\r\n    <p>\r\n        Custom sizes are available upon request, depending on project requirements.\r\n    </p>\r\n</div>\r\n', 'washers');

